mapper.select:
SELECT

FROM quant_swapavg_config a
WHERE 1=1


    and a.account_id = #{accountId}


    and a.status = #{status}


    and a.exchange_account = #{exchangeAccount}



    and a.currency = #{currency}

order by a.id desc

mapper.update:
update quant_swapavg_config set
    status =#{status}
where id = #{id}
